import Grid from "@material-ui/core/Grid";
import Card from '@material-ui/core/Card';
import LinearProgress from '@material-ui/core/LinearProgress';
// Custom components
import ViewOperationDayForm from "../ViewOperationDay/ViewOperationDay.jsx";
import Instructions from "../Instructions/Instructions.jsx";
import TreansferOrders from "../TreansferOrders/TreansferOrders.jsx";
import Users from "../Users/Users.jsx";
import ViewReport from "../ViewReport/ViewReport.jsx";
import MainForm from "../MainForm/MainForm.jsx";
import ViewInstructionsRegistrars from "../ViewInstructionsRegistrars/ViewInstructionsRegistrars.jsx";
