  // Convert date to approptiate format
  function beautifyDate(date) {
    try {
      var newDate = new Date(date) // "2020-01-26"
      var dd = String(newDate.getDate()).padStart(2, '0')
      var mm = String(newDate.getMonth() + 1).padStart(2, '0') //January is 0!
      var yyyy = newDate.getFullYear()
      let beautyDate = ""
      if (parseInt(dd) < 10) {
        let shortdd = dd.substring(1, 2)
        dd = shortdd + " "
      }
      else { beautyDate += dd }
      switch (mm) {
        case "01": {
          mm = "янв."
          break
        }
        case "02": {
          mm = "февр."
          break
        }
        case "03": {
          mm = "мар."
          break
        }
        case "04": {
          mm = "апр."
          break
        }
        case "05": {
          mm = "мая"
          break
        }
        case "06": {
          mm = "июня"
          break
        }
        case "07": {
          mm = "июля"
          break
        }
        case "08": {
          mm = "авг."
          break
        }
        case "09": {
          mm = "сент."
          break
        }
        case "10": {
          mm = "окт."
          break
        }
        case "11": {
          mm = "ноя."
          break
        }
        case "12": {
          mm = "дек."
          break
        }
        default: {
          break
        }
      }
      beautyDate += " " + mm + " " + yyyy.toString().substring(2, 4) + "г."
      // console.log("beautyDate", beautyDate)
      return beautyDate
    }
    catch (er) {
      return "NaN.NaN.NaN"
    }
  }
