  // filter accounts that can be selected by instruction type
  function checkForPushing(label, instructionTypeId) {
    let checkResult = {
      enableToPush: false,
      accountShortName: null
    }
    for (let i = 0; i < props.userTask.enumData.length; i++) {
      if (props.userTask.enumData[i].name === "instructionAccountRelations") {
        for (let d = 0; d < props.userTask.enumData[i].data.length; d++) {
          if (props.userTask.enumData[i].data[d].id === instructionTypeId) {
            let splitedAccountName = props.userTask.enumData[i].data[d].label.split("-")
            let accountShortName = splitedAccountName[1]
            let splitedAccountLabel = label.split("-")
            let shortLabel = splitedAccountLabel[1]
            if (shortLabel === accountShortName) {
              if (userProfile.userRole === "2" || userProfile.userRole === "3" || userProfile.userRole === "4" || userProfile.userRole === "8") {
                let accPartner = splitedAccountLabel[2]
                if (accPartner === userProfile.partner) {
                  checkResult["enableToPush"] = true
                  checkResult["accountShortName"] = accountShortName
                  break
                }
              }
              else {
                checkResult["enableToPush"] = true
                checkResult["accountShortName"] = accountShortName
                break
              }
            }
          }
        }
      }
    }
    return checkResult
  }
